import React, { useState } from 'react';
import { Grid, Typography, Paper, Snackbar, Alert } from '@mui/material';
import './KnockoutStage.css'; // Le fichier CSS pour la mise en forme

// Exemple de données des matchs avec des photos et des scores pour toutes les phases
const knockoutData = {
    "1/8e de finale": [
        { player1: { name: "Joueur 1", photo: "/images/profiles/joueur1.png", score: 3 }, player2: { name: "Joueur 2", photo: "/images/profiles/joueur2.png", score: 1 } },
        { player1: { name: "Joueur 3", photo: "/images/profiles/joueur3.png", score: 2 }, player2: { name: "Joueur 4", photo: "/images/profiles/joueur4.png", score: 4 } },
        { player1: { name: "Joueur 5", photo: "/images/profiles/joueur5.png", score: 1 }, player2: { name: "Joueur 6", photo: "/images/profiles/joueur6.png", score: 2 } },
        { player1: { name: "Joueur 7", photo: "/images/profiles/joueur7.png", score: 3 }, player2: { name: "Joueur 8", photo: "/images/profiles/joueur8.png", score: 5 } },
    ],
    // Autres phases (1/4, 1/2, Finale)...
};

const KnockoutStage = () => {
    const [selectedMatch, setSelectedMatch] = useState(null); // Pour suivre le match sélectionné
    const [openAlert, setOpenAlert] = useState(false); // Pour contrôler l'affichage de l'alerte

    // Fonction pour calculer les pourcentages pour les barres
    const calculatePercentage = (score1, score2) => {
        const total = score1 + score2;
        return {
            player1: (score1 / total) * 100,
            player2: (score2 / total) * 100
        };
    };

    // Fonction pour ouvrir le bandeau avec le score
    const handleMatchClick = (match) => {
        setSelectedMatch(match);
        setOpenAlert(true);
    };

    // Fonction pour fermer l'alerte
    const handleCloseAlert = () => {
        setOpenAlert(false);
    };

    return (
        <div className="knockout-stage-container">
            {Object.keys(knockoutData).map((roundName, roundIndex) => (
                <div key={roundIndex} className="round-container">
                    <Typography variant="h5" className="round-title">{roundName}</Typography>
                    <Grid container spacing={2} justifyContent="center">
                        {knockoutData[roundName].map((match, matchIndex) => {
                            return (
                                <Grid item xs={12} md={6} key={matchIndex}>
                                    <Paper
                                        className="match-container"
                                        onClick={() => handleMatchClick(match)}
                                    >
                                        <div className="match-info">
                                            <div className="player">
                                                <img src={match.player1.photo} alt={match.player1.name} className="player-photo" />
                                                <span className="player-name">{match.player1.name}</span>
                                            </div>
                                            <div className="player">
                                                <img src={match.player2.photo} alt={match.player2.name} className="player-photo" />
                                                <span className="player-name">{match.player2.name}</span>
                                            </div>
                                        </div>
                                    </Paper>
                                </Grid>
                            );
                        })}
                    </Grid>
                </div>
            ))}

            {/* Bandeau de score (alerte) */}
            {selectedMatch && (
                <Snackbar
                    open={openAlert}
                    autoHideDuration={6000} // Le bandeau reste affiché 6 secondes
                    onClose={handleCloseAlert}
                    anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
                >
                    <Alert onClose={handleCloseAlert} severity="info" sx={{ width: '100%' }}>
                        <div className="alert-content">
                            <div className="alert-title">
                                <span>{selectedMatch.player1.name} vs {selectedMatch.player2.name}</span>
                            </div>
                            <div className="score-bar">
                                <div className="score-bar-part red" style={{ width: `${calculatePercentage(selectedMatch.player1.score, selectedMatch.player2.score).player1}%` }} />
                                <div className="score-bar-part blue" style={{ width: `${calculatePercentage(selectedMatch.player1.score, selectedMatch.player2.score).player2}%` }} />
                            </div>
                        </div>
                    </Alert>
                </Snackbar>
            )}
        </div>
    );
};

export default KnockoutStage;
