import React from 'react';
import { Grid, Typography, Paper } from '@mui/material';
import './KnockoutStage.css'; // Le fichier CSS pour la mise en forme

// Exemple de données des matchs de phase éliminatoire
const knockoutData = {
    "1/8e de finale": [
        { player1: { name: "Joueur 1", score: 3 }, player2: { name: "Joueur 2", score: 1 } },
        { player1: { name: "Joueur 3", score: 2 }, player2: { name: "Joueur 4", score: 4 } },
        { player1: { name: "Joueur 5", score: 1 }, player2: { name: "Joueur 6", score: 2 } },
        { player1: { name: "Joueur 7", score: 3 }, player2: { name: "Joueur 8", score: 5 } },
        { player1: { name: "Joueur 9", score: 0 }, player2: { name: "Joueur 10", score: 3 } },
        { player1: { name: "Joueur 11", score: 1 }, player2: { name: "Joueur 12", score: 4 } },
        { player1: { name: "Joueur 13", score: 3 }, player2: { name: "Joueur 14", score: 2 } },
        { player1: { name: "Joueur 15", score: 2 }, player2: { name: "Joueur 16", score: 1 } },
    ],
    "1/4e de finale": [
        { player1: { name: "Joueur 1", score: 2 }, player2: { name: "Joueur 4", score: 3 } },
        { player1: { name: "Joueur 6", score: 0 }, player2: { name: "Joueur 8", score: 1 } },
        { player1: { name: "Joueur 10", score: 1 }, player2: { name: "Joueur 12", score: 4 } },
        { player1: { name: "Joueur 13", score: 3 }, player2: { name: "Joueur 15", score: 2 } },
    ],
    "1/2e de finale": [
        { player1: { name: "Joueur 4", score: 3 }, player2: { name: "Joueur 8", score: 1 } },
        { player1: { name: "Joueur 12", score: 2 }, player2: { name: "Joueur 13", score: 4 } },
    ],
    "Finale": [
        { player1: { name: "Joueur 4", score: 1 }, player2: { name: "Joueur 13", score: 3 } },
    ]
};

const KnockoutStage = () => {
    return (
        <div className="knockout-stage-container">
            {Object.keys(knockoutData).map((roundName, roundIndex) => (
                <div key={roundIndex} className="round-container">
                    <Typography variant="h5" className="round-title">{roundName}</Typography>
                    <Grid container spacing={2} justifyContent="center">
                        {knockoutData[roundName].map((match, matchIndex) => (
                            <Grid item xs={12} md={6} key={matchIndex}>
                                <Paper className="match-container">
                                    <div className="match-info">
                                        <div className={`player ${match.player1.score > match.player2.score ? 'winner' : ''}`}>
                                            <span>{match.player1.name}</span>
                                            <span className="score">{match.player1.score}</span>
                                        </div>
                                        <div className={`player ${match.player2.score > match.player1.score ? 'winner' : ''}`}>
                                            <span>{match.player2.name}</span>
                                            <span className="score">{match.player2.score}</span>
                                        </div>
                                    </div>
                                </Paper>
                            </Grid>
                        ))}
                    </Grid>
                </div>
            ))}
        </div>
    );
};

export default KnockoutStage;
