import React from 'react';
import { Grid, Typography, Paper } from '@mui/material';
import './Calendrier.css';

// Exemple de données de calendrier (journées, matchs avec dates, heures et scores)
const calendarData = [
    {
        date: "Vendredi 1 NOV 2024",
        matches: [
            { player1: { name: "Joueur 1", photo: "/images/profiles/userTiktok.png", score: 3 }, player2: { name: "Joueur 2", photo: "/images/profiles/userTiktok.png", score: 1 }, time: "18:00" },
            { player1: { name: "Joueur 3", photo: "/images/profiles/userTiktok.png", score: 2 }, player2: { name: "Joueur 4", photo: "/images/profiles/userTiktok.png", score: 4 }, time: "20:00" }
        ]
    },
    {
        date: "Samedi 2 NOV 2024",
        matches: [
            { player1: { name: "Joueur 5", photo: "/images/profiles/userTiktok.png", score: 1 }, player2: { name: "Joueur 6", photo: "/images/profiles/userTiktok.png", score: 2 }, time: "16:00" },
            { player1: { name: "Joueur 7", photo: "/images/profiles/userTiktok.png", score: 3 }, player2: { name: "Joueur 8", photo: "/images/profiles/userTiktok.png", score: 5 }, time: "18:00" },
            { player1: { name: "Joueur 9", photo: "/images/profiles/userTiktok.png", score: 0 }, player2: { name: "Joueur 10", photo: "/images/profiles/userTiktok.png", score: 3 }, time: "20:00" }
        ]
    },
    {
        date: "Dimanche 3 NOV 2024",
        matches: [
            { player1: { name: "Joueur 11", photo: "/images/profiles/userTiktok.png", score: 1 }, player2: { name: "Joueur 12", photo: "/images/profiles/userTiktok.png", score: 4 }, time: "17:00" },
            { player1: { name: "Joueur 13", photo: "/images/profiles/userTiktok.png", score: 2 }, player2: { name: "Joueur 14", photo: "/images/profiles/userTiktok.png", score: 3 }, time: "19:00" }
        ]
    }
];

const Calendar = () => {
    return (
        <div className="calendar-container">
            {calendarData.map((journey, journeyIndex) => (
                <div key={journeyIndex} className="journey-container">
                    <Typography variant="h5" className="journey-title">{`Journée ${journeyIndex + 1}: ${journey.date}`}</Typography>
                    <Grid container spacing={3} justifyContent="center">
                        {journey.matches.map((match, matchIndex) => {
                            const { player1, player2, time } = match;

                            return (
                                <Grid item xs={12} md={6} key={matchIndex}>
                                    <Paper className="match-container">
                                        <div className="match-info">
                                            {/* Joueur 1 */}
                                            <div className="player">
                                                <img src={player1.photo} alt={player1.name} className="player-photo" />
                                                <span className="player-name">{player1.name}</span>
                                            </div>

                                            <div className="versus-separator">VS</div>

                                            {/* Joueur 2 */}
                                            <div className="player">
                                                <img src={player2.photo} alt={player2.name} className="player-photo" />
                                                <span className="player-name">{player2.name}</span>
                                            </div>
                                        </div>

                                        {/* Heure du match */}
                                        <div className="match-time">{time}</div>

                                        {/* Affichage des scores si le match est terminé */}
                                        <div className="score-display">
                                            {`Score: ${player1.score} - ${player2.score}`}
                                        </div>
                                    </Paper>
                                </Grid>
                            );
                        })}
                    </Grid>
                </div>
            ))}
        </div>
    );
};

export default Calendar;
