import React, { useState } from 'react';
import { Grid, Typography, Paper, Dialog, DialogContent, DialogTitle, Button } from '@mui/material';
import './KnockoutStage.css'; // Le fichier CSS pour la mise en forme

// Exemple de données des matchs avec des photos et des scores pour toutes les phases
const knockoutData = {
    "1/8e de finale": [
        { player1: { name: "Joueur 1", photo: "/images/profiles/joueur1.png", score: 3 }, player2: { name: "Joueur 2", photo: "/images/profiles/joueur2.png", score: 1 } },
        { player1: { name: "Joueur 3", photo: "/images/profiles/joueur3.png", score: 2 }, player2: { name: "Joueur 4", photo: "/images/profiles/joueur4.png", score: 4 } },
        { player1: { name: "Joueur 5", photo: "/images/profiles/joueur5.png", score: 1 }, player2: { name: "Joueur 6", photo: "/images/profiles/joueur6.png", score: 2 } },
        { player1: { name: "Joueur 7", photo: "/images/profiles/joueur7.png", score: 3 }, player2: { name: "Joueur 8", photo: "/images/profiles/joueur8.png", score: 5 } },
        { player1: { name: "Joueur 9", photo: "/images/profiles/joueur9.png", score: 0 }, player2: { name: "Joueur 10", photo: "/images/profiles/joueur10.png", score: 3 } },
        { player1: { name: "Joueur 11", photo: "/images/profiles/joueur11.png", score: 1 }, player2: { name: "Joueur 12", photo: "/images/profiles/joueur12.png", score: 4 } },
        { player1: { name: "Joueur 13", photo: "/images/profiles/joueur13.png", score: 3 }, player2: { name: "Joueur 14", photo: "/images/profiles/joueur14.png", score: 2 } },
        { player1: { name: "Joueur 15", photo: "/images/profiles/joueur15.png", score: 2 }, player2: { name: "Joueur 16", photo: "/images/profiles/joueur16.png", score: 1 } },
    ],
    "1/4e de finale": [
        { player1: { name: "Joueur 1", photo: "/images/profiles/joueur1.png", score: 2 }, player2: { name: "Joueur 4", photo: "/images/profiles/joueur4.png", score: 3 } },
        { player1: { name: "Joueur 6", photo: "/images/profiles/joueur6.png", score: 1 }, player2: { name: "Joueur 8", photo: "/images/profiles/joueur8.png", score: 2 } },
        { player1: { name: "Joueur 10", photo: "/images/profiles/joueur10.png", score: 3 }, player2: { name: "Joueur 12", photo: "/images/profiles/joueur12.png", score: 4 } },
        { player1: { name: "Joueur 13", photo: "/images/profiles/joueur13.png", score: 5 }, player2: { name: "Joueur 15", photo: "/images/profiles/joueur15.png", score: 2 } },
    ],
    "1/2e de finale": [
        { player1: { name: "Joueur 4", photo: "/images/profiles/joueur4.png", score: 1 }, player2: { name: "Joueur 8", photo: "/images/profiles/joueur8.png", score: 3 } },
        { player1: { name: "Joueur 12", photo: "/images/profiles/joueur12.png", score: 2 }, player2: { name: "Joueur 13", photo: "/images/profiles/joueur13.png", score: 4 } },
    ],
    "Finale": [
        { player1: { name: "Joueur 8", photo: "/images/profiles/joueur8.png", score: 1 }, player2: { name: "Joueur 13", photo: "/images/profiles/joueur13.png", score: 3 } }
    ]
};

const KnockoutStage = () => {
    const [selectedMatch, setSelectedMatch] = useState(null); // Pour suivre le match sélectionné

    // Fonction pour calculer les pourcentages pour les barres
    const calculatePercentage = (score1, score2) => {
        const total = score1 + score2;
        return {
            player1: (score1 / total) * 100,
            player2: (score2 / total) * 100
        };
    };

    // Fonction pour ouvrir le bandeau avec le score
    const handleMatchClick = (match) => {
        setSelectedMatch(match);
    };

    // Fonction pour fermer le bandeau
    const handleCloseDialog = () => {
        setSelectedMatch(null);
    };

    return (
        <div className="knockout-stage-container">
            {Object.keys(knockoutData).map((roundName, roundIndex) => (
                <div key={roundIndex} className="round-container">
                    <Typography variant="h5" className="round-title">{roundName}</Typography>
                    <Grid container spacing={2} justifyContent="center">
                        {knockoutData[roundName].map((match, matchIndex) => (
                            <Grid item xs={12} md={6} key={matchIndex}>
                                <Paper
                                    className="match-container"
                                    onClick={() => handleMatchClick(match)}
                                >
                                    <div className="match-info">
                                        <div className="player">
                                            <img src={match.player1.photo} alt={match.player1.name} className="player-photo" />
                                            <span className="player-name">{match.player1.name}</span>
                                        </div>
                                        <div className="player">
                                            <img src={match.player2.photo} alt={match.player2.name} className="player-photo" />
                                            <span className="player-name">{match.player2.name}</span>
                                        </div>
                                    </div>
                                </Paper>
                            </Grid>
                        ))}
                    </Grid>
                </div>
            ))}

            {/* Bandeau de score (dialogue centré) */}
            {selectedMatch && (
                <Dialog
                    open={!!selectedMatch}
                    onClose={handleCloseDialog}
                    fullWidth
                    maxWidth="md"
                    className="score-dialog"
                >
                    <DialogTitle>
                        <Typography variant="h6" align="center">
                            {selectedMatch.player1.name} vs {selectedMatch.player2.name}
                        </Typography>
                    </DialogTitle>
                    <DialogContent>
                        <div className="dialog-content">
                            <div className="score-details">
                                <span className="score-number red">{selectedMatch.player1.score}k</span>
                                <span className="score-number blue">{selectedMatch.player2.score}k</span>
                            </div>
                            <div className="score-bar">
                                <div
                                    className="score-bar-part red"
                                    style={{ width: `${calculatePercentage(selectedMatch.player1.score, selectedMatch.player2.score).player1}%` }}
                                />
                                <div
                                    className="score-bar-part blue"
                                    style={{ width: `${calculatePercentage(selectedMatch.player1.score, selectedMatch.player2.score).player2}%` }}
                                />
                            </div>
                            <Button
                                variant="contained"
                                color="primary"
                                fullWidth
                                onClick={handleCloseDialog}
                                style={{ marginTop: '20px' }}
                            >
                                Fermer
                            </Button>
                        </div>
                    </DialogContent>
                </Dialog>
            )}
        </div>
    );
};

export default KnockoutStage;
