import React from 'react';
import { Grid, Typography, Paper } from '@mui/material';
import './KnockoutStage.css'; // Le fichier CSS pour la mise en forme

// Exemple de données des matchs avec des photos et des scores
const knockoutData = {
    "1/8e de finale": [
        { player1: { name: "Joueur 1", photo: "/images/profiles/joueur1.png", score: 3 }, player2: { name: "Joueur 2", photo: "/images/profiles/joueur2.png", score: 1 } },
        { player1: { name: "Joueur 3", photo: "/images/profiles/joueur3.png", score: 2 }, player2: { name: "Joueur 4", photo: "/images/profiles/joueur4.png", score: 4 } },
        { player1: { name: "Joueur 5", photo: "/images/profiles/joueur5.png", score: 1 }, player2: { name: "Joueur 6", photo: "/images/profiles/joueur6.png", score: 2 } },
        { player1: { name: "Joueur 7", photo: "/images/profiles/joueur7.png", score: 3 }, player2: { name: "Joueur 8", photo: "/images/profiles/joueur8.png", score: 5 } },
        { player1: { name: "Joueur 9", photo: "/images/profiles/joueur9.png", score: 0 }, player2: { name: "Joueur 10", photo: "/images/profiles/joueur10.png", score: 3 } },
        { player1: { name: "Joueur 11", photo: "/images/profiles/joueur11.png", score: 1 }, player2: { name: "Joueur 12", photo: "/images/profiles/joueur12.png", score: 4 } },
        { player1: { name: "Joueur 13", photo: "/images/profiles/joueur13.png", score: 3 }, player2: { name: "Joueur 14", photo: "/images/profiles/joueur14.png", score: 2 } },
        { player1: { name: "Joueur 15", photo: "/images/profiles/joueur15.png", score: 2 }, player2: { name: "Joueur 16", photo: "/images/profiles/joueur16.png", score: 1 } },
    ],
    // Ajoute les données pour les autres rounds...
    "Finale": [
        { player1: { name: "Joueur 4", photo: "/images/profiles/joueur4.png", score: 1 }, player2: { name: "Joueur 13", photo: "/images/profiles/joueur13.png", score: 3 } }
    ]
};

const KnockoutStage = () => {
    return (
        <div className="knockout-stage-container">
            {Object.keys(knockoutData).map((roundName, roundIndex) => (
                <div key={roundIndex} className="round-container">
                    <Typography variant="h5" className="round-title">{roundName}</Typography>
                    <Grid container spacing={2} justifyContent="center">
                        {knockoutData[roundName].map((match, matchIndex) => (
                            <Grid item xs={12} md={6} key={matchIndex}>
                                <Paper className="match-container">
                                    <div className="match-info">
                                        <div className={`player ${match.player1.score > match.player2.score ? 'winner' : ''}`}>
                                            <img src={match.player1.photo} alt={match.player1.name} className="player-photo" />
                                            <span className="player-name">{match.player1.name}</span>
                                            <span className="score">{match.player1.score}</span>
                                        </div>
                                        <div className={`player ${match.player2.score > match.player1.score ? 'winner' : ''}`}>
                                            <img src={match.player2.photo} alt={match.player2.name} className="player-photo" />
                                            <span className="player-name">{match.player2.name}</span>
                                            <span className="score">{match.player2.score}</span>
                                        </div>
                                    </div>
                                </Paper>
                            </Grid>
                        ))}
                    </Grid>
                </div>
            ))}
        </div>
    );
};

export default KnockoutStage;
