import React from 'react';
import { Grid, Grow, Card, CardContent, Typography, Paper } from '@mui/material';
import './KnockoutStage.css'; // Pour styliser la phase éliminatoire

// Composant AnimatedCard pour afficher chaque match avec une animation "Grow"
const AnimatedCard = ({ match }) => (
    <Grow in={true} timeout={1000}>
        <Card>
            <CardContent>
                <Typography variant="h6">{match.round}</Typography>
                <Typography>{match.team1} vs {match.team2}</Typography>
                <Typography>Score : {match.score}</Typography>
            </CardContent>
        </Card>
    </Grow>
);

// Exemple de données des matchs (phase éliminatoire)
const knockoutData = [
    { round: "Quart de Finale", matches: [
            { player1: { name: "Joueur 1", score: 2 }, player2: { name: "Joueur 2", score: 1 } },
            { player1: { name: "Joueur 3", score: 3 }, player2: { name: "Joueur 4", score: 2 } },
            { player1: { name: "Joueur 5", score: 1 }, player2: { name: "Joueur 6", score: 2 } },
            { player1: { name: "Joueur 7", score: 4 }, player2: { name: "Joueur 8", score: 3 } }
        ]},
    { round: "Demi-Finale", matches: [
            { player1: { name: "Joueur 1", score: 2 }, player2: { name: "Joueur 3", score: 3 } },
            { player1: { name: "Joueur 6", score: 0 }, player2: { name: "Joueur 7", score: 1 } }
        ]},
    { round: "Finale", matches: [
            { player1: { name: "Joueur 3", score: 2 }, player2: { name: "Joueur 7", score: 1 } }
        ]}
];

const KnockoutStage = () => {
    return (
        <div className="knockout-stage-container">
            {knockoutData.map((roundData, index) => (
                <div key={index} className="round-container">
                    <Typography variant="h5" className="round-title">{roundData.round}</Typography>
                    <Grid container spacing={2} justifyContent="center">
                        {roundData.matches.map((match, idx) => (
                            <Grid item xs={12} md={6} key={idx}>
                                <Paper className="match-container">
                                    <div className="match-info">
                                        <div className={`player ${match.player1.score > match.player2.score ? 'winner' : ''}`}>
                                            <span>{match.player1.name}</span>
                                            <span className="score">{match.player1.score}</span>
                                        </div>
                                        <div className={`player ${match.player2.score > match.player1.score ? 'winner' : ''}`}>
                                            <span>{match.player2.name}</span>
                                            <span className="score">{match.player2.score}</span>
                                        </div>
                                    </div>
                                </Paper>
                            </Grid>
                        ))}
                    </Grid>
                </div>
            ))}
        </div>
    );
};

export default KnockoutStage;
